import styled from 'styled-components';
import { darken } from 'polished';

export const Wrapper = styled.div`
  height: 100%;
  background: linear-gradient(-90deg, #9954ff, #ab59c1);
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Content = styled.div`
  max-width: 300px;
  background: #fff;
  border-radius: 4px;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
  padding: 30px;
  margin: 80px auto;
  align-items: center;
  display: flex;
  flex-direction: column;

  h1 {
    font-size: 20px;
    display: flex;
    flex-direction: column;
  }

  input {
    display: block;
    margin: auto auto;
    width: 100%;
    margin-bottom: 2em;
    padding: 0.5em 0;
    border: none;
    border-bottom: 1px solid #000001;
    padding-bottom: 1.25em;
    color: #757575;
  }

  button {
    color: #ffffff;
    font-size: 20px;
    text-align: center;
    width: 250px;
    height: 40px;
    border: 0px;
    border-radius: 10px;
    background: #9954ff;

    &:hover {
      background: ${darken(0.03, '#9954ff')};
    }
  }
`;
