import styled from 'styled-components';

export const Container = styled.div`
  max-width: 300px;
  background: #fff;
  border-radius: 4px;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
  padding: 30px;
  margin: 80px auto;
  align-items: center;
  display: flex;
  flex-direction: column;

  h1 {
    font-size: 20px;
    display: flex;
    flex-direction: column;
  }
`;

export const Input = styled.input`
  outline: none;
  border: none;
  border-bottom: #000;
`;
